var options = $('#[QUESTION]i1 option');
if (options.length == 2) {
  $('#[QUESTION]i1 option:eq(1)').attr('selected', 'selected');  
  $('#[QUESTION]i1').prop("disabled", true);
  $('#[QUESTION]i1').attr("name", "");

  var hid = $('<input type="hidden" name="[QUESTION]i1" >')
  hid.val($('#[QUESTION]i1').val());
  hid.appendTo($('#[PAGE]'));
}
