data.javascript:
var authority_element = $('#authority.[QUESTION], #[QUESTION]i1')
var options = authority_element.find('option')
options.removeAttr('selected')

if (options.length == 2) { 
  $(authority_element).prop("disabled", true);
  $(options[1]).attr('selected', 'selected')

  var hid = $('<input type="hidden" name="[QUESTION]i1" >')
  hid.val($(options[1]).val());
  hid.appendTo($('#[PAGE]'));
}
