var authority_section = $('#authority, #[QUESTION]i1')
var options = authority_section.find('option')
options.removeAttr('selected')

if (options.length == 2) { 
  $(authority_section).prop("disabled", true);
  $(options[1]).attr('selected', 'selected')

  var hid = $('<input type="hidden" name="c3q5i1" >')
  hid.val($(options[1]).val());
  hid.appendTo($('#[PAGE]'));
}
