	"INSTANCE_ID",
	"DOSSIERNR"."ANSWER" as "DOSSIER_NR",
	"FORM"."NAME"        as "FORM",
	"LOCATION"."NAME"    as "COMMUNITY",
	"USER"."USERNAME"        AS "USER",
	GET_STATE_NAME_BY_ID("INSTANCE"."INSTANCE_STATE_ID") AS "STATUS"


	
FROM "INSTANCE"

JOIN "INSTANCE_LOCATION" ON (
		"INSTANCE"."INSTANCE_ID" = "INSTANCE_LOCATION"."INSTANCE_ID"
)

JOIN "LOCATION" ON (
		"INSTANCE_LOCATION"."LOCATION_ID" = "LOCATION"."LOCATION_ID"
)

join "ANSWER"  "DOSSIERNR" ON (
		"DOSSIERNR"."INSTANCE_ID" = "INSTANCE"."INSTANCE_ID"
		AND
		"DOSSIERNR"."QUESTION_ID" = 6
		AND
		"DOSSIERNR"."CHAPTER_ID" = 2
		AND
		"DOSSIERNR"."ITEM" = 1
	)

join "FORM" ON (
	"INSTANCE"."FORM_ID" = "FORM"."FORM_ID"
)

JOIN "USER" ON (
		"INSTANCE"."USER_ID" = "USER"."USER_ID"
)');
	"INSTANCE"."INSTANCE_ID" AS "INSTANCE_ID",
	"ANSWER"."ANSWER"        AS "DOSSIER_NR",
	"FORM"."NAME"            AS "FORM",
	"LOCATION"."NAME"        AS "COMMUNITY",
	"USER"."USERNAME"        AS "USER"

FROM
	"INSTANCE"
JOIN
	"INSTANCE_LOCATION" ON (
		"INSTANCE"."INSTANCE_ID" = "INSTANCE_LOCATION"."INSTANCE_ID"
	)

JOIN 
	"LOCATION" ON (
	 "INSTANCE_LOCATION"."LOCATION_ID" = "LOCATION"."LOCATION_ID"
	)

JOIN
	FORM ON (
		"INSTANCE"."FORM_ID" = "FORM"."FORM_ID"
)

JOIN
	"USER" ON (
		"INSTANCE"."USER_ID" = "USER"."USER_ID"
)

JOIN
	"ANSWER" ON (
		"INSTANCE"."INSTANCE_ID" = "ANSWER"."INSTANCE_ID"
		AND
		"ANSWER"."QUESTION_ID" = 6
		AND
		"ANSWER"."CHAPTER_ID" = 2
		AND
		"ANSWER"."ITEM" = 1
	)
WHERE
	"INSTANCE"."INSTANCE_STATE_ID" = 21
	AND
	"INSTANCE_LOCATION"."LOCATION_ID" IN (
		SELECT
			"LOCATION_ID"
		FROM
			"GROUP_LOCATION"
		WHERE
			"GROUP_ID" = [GROUP_ID]
	)');
	"INSTANCE"."INSTANCE_ID" AS "INSTANCE_ID",
	"ANSWER"."ANSWER"        AS "DOSSIER_NR",
	"FORM"."NAME"            AS "FORM",
	"LOCATION"."NAME"        AS "COMMUNITY",
	"USER"."USERNAME"        AS "USER"

FROM
	"INSTANCE"
JOIN
	"INSTANCE_LOCATION" ON (
		"INSTANCE"."INSTANCE_ID" = "INSTANCE_LOCATION"."INSTANCE_ID"
	)

JOIN 
	"LOCATION" ON (
	 "INSTANCE_LOCATION"."LOCATION_ID" = "LOCATION"."LOCATION_ID"
	)

JOIN
	FORM ON (
		"INSTANCE"."FORM_ID" = "FORM"."FORM_ID"
)

JOIN
	"USER" ON (
		"INSTANCE"."USER_ID" = "USER"."USER_ID"
)

JOIN
	"ANSWER" ON (
		"INSTANCE"."INSTANCE_ID" = "ANSWER"."INSTANCE_ID"
		AND
		"ANSWER"."QUESTION_ID" = 6
		AND
		"ANSWER"."CHAPTER_ID" = 2
		AND
		"ANSWER"."ITEM" = 1
	)
JOIN 
	"INSTANCE_STATE" ON (
	"INSTANCE_STATE"."NAME" = ''ext''
)
WHERE
	"INSTANCE"."INSTANCE_STATE_ID" = "INSTANCE_STATE"."INSTANCE_STATE_ID"');

from instance

join instance_state

on instance.instance_state_id = instance_state.instance_state_id

join circulation

on circulation.instance_id = instance.instance_id

join activation

on activation.circulation_id = circulation.circulation_id

and service_id = [SERVICE_ID]

join "FORM" on instance.form_id = form.form_id

where (instance.instance_state_id = 23)


order by instance_id');
